--
-- Table structure for table `images`
--

DROP TABLE IF EXISTS `images`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `images` (
  `id` int(6) NOT NULL auto_increment,
  `id_category` int(5) default NULL,
  `number` int(5) NOT NULL,
  `img_path` varchar(250) default NULL,
  `img_name` varchar(250) NOT NULL,
  `img_year` year(4) NOT NULL,
  `img_material` varchar(250) NOT NULL,
  `img_size` varchar(20) NOT NULL,
  `img_comment` varchar(350) NOT NULL,
  `sell_price` int(6) NOT NULL,
  `status` varchar(4) NOT NULL,
  PRIMARY KEY  (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=389 DEFAULT CHARSET=cp1251;
/*!40101 SET character_set_client = @saved_cs_client */;

